<script>
import base from "@/myconfig/base.js";
export default {
  data() {
    return {
      info: {},
      can:[],
      show: false,
      base:base,
      num:1,
      firstindex:0,
      secondindex:0,
      price:0,
      images: [
        "http://img1.imgtn.bdimg.com/it/u=2658928701,3549149282&fm=26&gp=0.jpg",
        "http://img1.imgtn.bdimg.com/it/u=2658928701,3549149282&fm=26&gp=0.jpg",
        "http://img1.imgtn.bdimg.com/it/u=2658928701,3549149282&fm=26&gp=0.jpg",
        "http://img1.imgtn.bdimg.com/it/u=2658928701,3549149282&fm=26&gp=0.jpg"
      ]
      //规格
    };
  },
  methods: {
    buy() {
      this.show = true;
    },
    count(can){
      if(can==1){
        this.num+=1;
      }else{
        if( this.num>0){
        this.num-=1;
        }else{
          alert("数量已为最小值")
        }
      }
       this.info.num=this.num;
    },
    realbuy(){
      let {_id,goodsname,size}=this.info;
      let obj={
        _id:_id,
        goodsname:goodsname,
        price:this.price,
        minisrc:this.info.minisrc,
        kind:this.info.size[this.firstindex].name,
        size:this.can[this.secondindex].itemname,
        num:this.num
      }
      this.$store.commit("count",obj)
      this.$router.push({name:"car"})
    },
    onSubmit() {},
    left() {
      this.$router.go(-1);
    },
    right() {
      this.$router.push({ name: "search" });
    },
    first(index){
      this.firstindex=index;
      this.can=this.info.size[index].item;
    },
    second(index){
      this.secondindex=index;
      this.price=this.can[index].price
       console.log(this.price)
    }
  },
  mounted() {
    let id = this.$route.params.id;
    this.axios
      .get(`${base.url}/infobyid`, {
        params: {
          id: id
        }
      })
      .then(res => {
        this.info = res.data.data[0];
        this.info.num=this.num;
        this.can=this.info.size[0].item;
        // console.log(this.info);
      });
  }
};
</script>
